import styled, { createGlobalStyle } from 'styled-components';

export const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
  background-color: rgba(34, 37, 75, 1);
  }
`;

export const appContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

export const movieContainer = styled.div`
  background-color: rgba(55, 59, 105, 1);
  color: white;
  width: 250px;
  margin: 16px;
  border-radius: 5px;
  box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.1);
  position: relative;

  &:hover.overview-container-wrapper {
    display: flex;
  }
`;

// export const mvContainerImg = styled.div`
//   ${(props)=>css`
//     background-image: ;
//   `}
//   width: 100%;
// `;

export const movieInfo = styled.div`
  display: flex;
  padding: 20px;
  justify-content: space-between;
  align-items: center;
`;

export const mvInfoSpan = styled.span`
  margin-left: 3px;
`;

export const overviewContainerWrapper = styled.div`
  display: none;
  position: absolute;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.75);
  height: 100%;
  padding: 30px 27px;
  color: rgba(255, 255, 255, 0.677);
  overflow: auto;

  ::-webkit-scrollbar {
    width: 5px;
  }

  ::-webkit-scrollbar-thumb {
    height: 20%;
    background-color: grey;
    border-radius: 10px;
  }
`;

export const overviewContainerH4 = styled.h4`
  margin-bottom: 20px;
`;
