import styled from 'styled-components';

export const MovieContainerBox = styled.div`
  background-color: rgba(55, 59, 105, 1);
  color: white;
  width: 250px;
  margin: 16px;
  border-radius: 5px;
  box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.1);
  position: relative;

  img {
    width: 100%;
  }
`;

export const MovieInfoBox = styled.div`
  display: flex;
  padding: 20px;
  justify-content: space-between;
  align-items: center;

  span {
    margin-left: 3px;
  }
`;
