// React에서 Movie.jsx 파일을 인식하기 위해 아래와 같이 import 해줘야함
import React from 'react';
import * as M from './movie.stlye';

// img url 앞에 다 붙이기 위해 선언
const BaseUrl = 'https://image.tmdb.org/t/p/w1280/';

function Movie({ title, poster_path, vote_average, overview }) {
  return (
    // className = class
    <M.movieContainer>
      <img src={BaseUrl + poster_path} alt='포스터사진' />
      <M.overviewContainerWrapper>
        <div className='overview-container'>
          <M.overviewContainerH4>{title}</M.overviewContainerH4>
          <p>{overview}</p>
        </div>
      </M.overviewContainerWrapper>
      <M.movieInfo>
        <h4>{title}</h4>
        <M.mvInfoSpan>{vote_average}</M.mvInfoSpan>
      </M.movieInfo>
    </M.movieContainer>
  );
}

export default Movie;
