import React from 'react';
import styled from 'styled-components';

const MovieContainerBox = styled.div`
  background-color: rgba(55, 59, 105, 1);
  color: white;
  width: 250px;
  margin: 16px;
  border-radius: 5px;
  box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.1);
  position: relative;

  img {
    width: 100%;
  }
`;

// img url 앞에 다 붙이기 위해 선언
const BaseUrl = 'https://image.tmdb.org/t/p/w1280/';

function Movie({ title, poster_path, vote_average, overview }) {
  return (
    // className = class
    <MovieContainerBox>
      <img src={BaseUrl + poster_path} alt='포스터사진' />
      <div className='overview-container-wrapper'>
        <div className='overview-container'>
          <h4>{title}</h4>
          <p>{overview}</p>
        </div>
      </div>
      <div className='movie-info'>
        <h4>{title}</h4>
        <span>{vote_average}</span>
      </div>
    </MovieContainerBox>
  );
}

export default Movie;
