import React from 'react';
import { StyledInput } from './InfoBoxStyled';
import { useState, useEffect } from 'react';

export default function InfoBox({ props }) {
  const [info, setInfo] = useState({
    title: '',
    className: '',
    type: '',
    placeholder: '',
    errormsg: '',
  });

  useEffect(() => {
    if (props === 'email') {
      setInfo({
        title: '이메일 주소',
        className: 'email-input',
        type: 'email',
        placeholder: '아메일을 입력하세요',
        errormsg: '올바른 이메일을 입력해주세요.',
      });
    } else if (props === 'pw') {
      setInfo({
        title: '비밀번호',
        className: 'pw-input',
        type: 'password',
        placeholder: '영문, 숫자, 특수문자 포함 8자 이상',
      });
    }
  }, [props]);

  return (
    <div>
      <StyledInput
        className={info.className}
        type={info.type}
        placeholder={info.placeholder}
      />
    </div>
  );
}
