import styled from 'styled-components';

export const StyledInput = styled.input`
  border: 1px solid black;
  padding: 10px 15px;
  outline: none;
  box-shadow: none;
  border-radius: 8px;
  width: 65vh;
  height: 8vh;
  font-size: 20px;
  display: block;
`;

export const InfoForm = styled.form`
  margin-left: 30px;
`;

export const InfoTitle = styled.label`
  display: block;
  font-weight: bold;
  margin-bottom: 10px;
`;

export const WarningMsg = styled.div`
  font-weight: bold;
  color: red;
  margin-top: 10px;
  margin-bottom: 20px;
`;
