import styled from 'styled-components';

export const StyledInput = styled.input`
  border: 1px solid black;
  padding: 10px 15px;
  outline: none;
  box-shadow: none;
  border-radius: 8px;
  width: 65vh;
  height: 8vh;
  font-size: 20px;
`;

export const infoTitle = styled.label`
  display: block;
  font-weight: bold;
`;

export const warningMsg = styled.div`
  display: inline-block;
  font-weight: bold;
  color: red;
`;
