import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { StyledInput, InfoForm, InfoTitle, WarningMsg, SubmitBtn, NotCorrectBtn } from './InfoBoxStyled';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { setId, setPassword } from '../../redux/loginRedux';

export default function InfoBox() {
  const [user, setUser] = useState('');
  const [showIdErr, setshowIdErr] = useState(false);
  const [pw, setPw] = useState('');
  const [showPwErr, setshowPwErr] = useState(false);
  const [correct, setCorrect] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const userId = useSelector((state) => state.login.userId);
  const userPw = useSelector((state) => state.login.userPw);

  const handleIdChange = (e) => {
    setUser(e.target.value);

    if (e.target.value) setshowIdErr(false);
    else setshowIdErr(true);
    dispatch(setId(e.target.value));
  };

  const handlePwChange = (e) => {
    setPw(e.target.value);

    if (e.target.value) setshowPwErr(false);
    else setshowPwErr(true);
    dispatch(setPassword(e.target.value));
  };

  useEffect(() => {
    if (!showIdErr && !showPwErr && user.trim() !== '' && pw.trim() !== '') {
      setCorrect(true);
    } else {
      setCorrect(false);
    }
  }, [showIdErr, showPwErr, user, pw]);

  const handleSubmit = (e) => {
    e.preventDefault(); // 제출눌렀을때 새로고침이 되지않게 설정
    console.log('id:', user);
    console.log('Password:', pw);
    alert('로그인 완료!');
    navigate('/');
  };

  const PostData = async () => {
    try {
      const endpoint = 'http://localhost:3000/user/login';
      const requestBody = {
        id: userId,
        pw: userPw,
      };

      // axios를 사용하여 POST 요청 보내기
      const response = await axios.post(endpoint, requestBody, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      // 응답 데이터 확인
      console.log(response.data);
    } catch (error) {
      // 오류 처리
      console.error('Error during POST request:', error);
    }
  };

  const handleButtonClick = () => {
    PostData();
  };

  return (
    <div>
      <InfoForm className='info-form'>
        <InfoTitle for='id-input'>이메일 주소</InfoTitle>
        <StyledInput id='id-input' type='id' placeholder='아이디를 입력하세요' value={user} onChange={handleIdChange} />
        {showIdErr ? <WarningMsg>올바른 아이디를 입력해주세요.</WarningMsg> : <WarningMsg>&nbsp;</WarningMsg>}

        <InfoTitle for='pw-input'>비밀번호</InfoTitle>
        <StyledInput
          id='pw-input'
          type='password'
          placeholder='영문, 숫자, 특수문자 포함 8자 이상'
          value={pw}
          onChange={handlePwChange}
        />
        {showPwErr ? <WarningMsg>올바른 비밀번호를 입력해주세요.</WarningMsg> : <WarningMsg>&nbsp;</WarningMsg>}
        {correct ? (
          <SubmitBtn
            type='submit'
            onClick={(e) => {
              handleSubmit(e);
              handleButtonClick();
            }}
          >
            확인
          </SubmitBtn>
        ) : (
          <NotCorrectBtn>확인</NotCorrectBtn>
        )}
      </InfoForm>
    </div>
  );
}
