import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { StyledInput, InfoForm, InfoTitle, WarningMsg, SubmitBtn, NotCorrectBtn } from './InfoBoxStyled';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { setId, setPw } from '../redux/loginRedux';

export default function InfoBox() {
  const [email, setEmail] = useState('');
  const [showEmailErr, setshowEmailErr] = useState(false);
  const [pw, setPw] = useState('');
  const [showPwErr, setshowPwErr] = useState(false);
  const [correct, setCorrect] = useState(false);
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleEmailChange = (e) => {
    setEmail(e.target.value);

    const emailRegex = /\S+@\S+\.\S+/;
    if (!emailRegex.test(e.target.value)) setshowEmailErr(true);
    else setshowEmailErr(false);
  };

  const handlePwChange = (e) => {
    setPw(e.target.value);

    const pwRegex = /^(?=.*[A-Za-z])(?=.*\d)(?=.*[$@$!%*#?&])[A-Za-z\d$@$!%*#?&]{8,}$/;
    if (!pwRegex.test(e.target.value)) setshowPwErr(true);
    else setshowPwErr(false);
  };

  const handleBtn = (e) => {
    e.preventDefault();
    if ((email.trim() === '' && pw.trim() === '') || (showEmailErr && showPwErr)) {
      alert('이메일과 비밀번호를 올바르지 않습니다.');
    } else if (!showPwErr && email.trim() === '' && pw.trim() !== '') {
      alert('이메일이 올바르지 않습니다.');
    } else if (!showEmailErr && email.trim() !== '' && pw.trim() === '') {
      alert('비밀번호가 올바르지 않습니다.');
    }
  };

  useEffect(() => {
    if (!showEmailErr && !showPwErr && email.trim() !== '' && pw.trim() !== '') {
      setCorrect(true);
      PostData();
    } else {
      setCorrect(false);
    }
  }, [showEmailErr, showPwErr, email, pw]);

  const handleSubmit = (e) => {
    e.preventDefault(); // 제출눌렀을때 새로고침이 되지않게 설정
    console.log('Email:', email);
    console.log('Password:', pw);
    alert('로그인 완료!');
    navigate('/');
  };

  const userId = useSelector((state) => state.login.userId);
  const userPw = useSelector((state) => state.login.pw);

  const PostData = async () => {
    try {
      const endpoint = 'http://localhost:8080/user/login';
      const requestBody = {
        id: userId,
        pw: userPw,
      };

      // axios를 사용하여 POST 요청 보내기
      const response = await axios.post(endpoint, requestBody, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      // 응답 데이터 확인
      console.log(response);
    } catch (error) {
      // 오류 처리
      console.error('Error during POST request:', error);
    }
  };

  return (
    <div>
      <InfoForm className='info-form'>
        <InfoTitle for='email-input'>이메일 주소</InfoTitle>
        <StyledInput
          id='email-input'
          type='email'
          placeholder='아메일을 입력하세요'
          value={email}
          onChange={handleEmailChange}
        />
        {showEmailErr ? <WarningMsg>올바른 이메일을 입력해주세요.</WarningMsg> : <WarningMsg>&nbsp;</WarningMsg>}

        <InfoTitle for='pw-input'>비밀번호</InfoTitle>
        <StyledInput
          id='pw-input'
          type='password'
          placeholder='영문, 숫자, 특수문자 포함 8자 이상'
          value={pw}
          onChange={handlePwChange}
        />
        {showPwErr ? <WarningMsg>올바른 비밀번호를 입력해주세요.</WarningMsg> : <WarningMsg>&nbsp;</WarningMsg>}
        {correct ? (
          <SubmitBtn type='submit' onClick={handleSubmit}>
            확인
          </SubmitBtn>
        ) : (
          <NotCorrectBtn onClick={handleBtn}>확인</NotCorrectBtn>
        )}
      </InfoForm>
    </div>
  );
}
