import React, { useState } from 'react';
import axios from 'axios';
import * as S from './StyledInput';

export default function Input() {
  const [location, setLocation] = useState('');
  const [result, setResult] = useState({});

  const API_KEY = 'a66da37fecbb863cf1edd6b7e4e806d7';
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${API_KEY}`;

  const searchWeather = async (e) => {
    if (e.key === 'Enter') {
      try {
        const { data } = await axios.get(url); // 수정: axios.get에서 응답 데이터를 구조 분해할당하여 사용
        setResult(data);
        console.log(data);
      } catch (err) {
        alert(err);
      }
    }
  };

  return (
    <div style={{ display: 'flex', height: '100vh', justifyContent: 'center', alignItems: 'center' }}>
      <S.InputTag
        type='text'
        value={location}
        placeholder='도시를 입력하세요'
        onChange={(e) => setLocation(e.target.value)}
        onKeyDown={searchWeather}
      />
      <div>
        {Object.keys(result).length !== 0 && (
          <div>
            <p>도시명: {result.data.name}</p>
            <p>온도: {result.data.main.temp}</p>
            <p>날씨: {result.data.weather[0].main}</p>
          </div>
        )}
      </div>
    </div>
  );
}
